import React, { Component } from 'react'

export const DataContext = React.createContext();

export class DataProvider extends Component {

    state = {
        products: [
            {
                "_id": "1",
                "title": "Nike",
                "src": "https://cdn-images.farfetch-contents.com/13/74/19/54/13741954_21620872_1000.jpg",
                "description": "Lorem ipsum dolor sit, amet consectetur adipisicing elit.",
                "content": "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quasi labore ea exercitationem assumenda, minus ad.",
                "price": 23,
                "colors": ["red", "green", "blue"],
                "count": 1
            },
            {
                "_id": "2",
                "title": "Nike",
                "src": "https://static.nike.com/a/images/t_default/rkoc7hlnhmx3vayk88i6/%D0%BA%D1%80%D0%BE%D1%81%D1%81%D0%BE%D0%B2%D0%BA%D0%B8-air-heights-m1fkdz.png",
                "description": "Lorem ipsum dolor sit, amet consectetur adipisicing elit.",
                "content": "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quasi labore ea exercitationem assumenda, minus ad.",
                "price": 47,
                "colors": ["black", "crimson", "blue"],
                "count": 1
            },
            {
                "_id": "3",
                "title": "Nike",
                "src": "https://static.nike.com/a/images/t_default/rkoc7hlnhmx3vayk88i6/%D0%BA%D1%80%D0%BE%D1%81%D1%81%D0%BE%D0%B2%D0%BA%D0%B8-air-heights-m1fkdz.png",
                "description": "Lorem ipsum dolor sit, amet consectetur adipisicing elit.",
                "content": "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quasi labore ea exercitationem assumenda, minus ad.",
                "price": 55,
                "colors": ["red", "green", "black", "crimson",],
                "count": 1
            },
            {
                "_id": "4",
                "title": "Nike",
                "src": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTA-rQHJ0YrIMxOg_JoSuHx2iYCk3xQtpOYyw&usqp=CAU",
                "description": "Lorem ipsum dolor sit, amet consectetur adipisicing elit.",
                "content": "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quasi labore ea exercitationem assumenda, minus ad.",
                "price": 35,
                "colors": ["red", "black", "crimson", "blue"],
                "count": 1
            },
            {
                "_id": "5",
                "title": "Nike",
                "src": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTA-rQHJ0YrIMxOg_JoSuHx2iYCk3xQtpOYyw&usqp=CAU",
                "description": "Lorem ipsum dolor sit, amet consectetur adipisicing elit.",
                "content": "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quasi labore ea exercitationem assumenda, minus ad.",
                "price": 37,
                "colors": ["red", "black", "crimson", "blue"],
                "count": 1
            },
            {
                "_id": "6",
                "title": "Nike",
                "src": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTA-rQHJ0YrIMxOg_JoSuHx2iYCk3xQtpOYyw&usqp=CAU",
                "description": "Lorem ipsum dolor sit, amet consectetur adipisicing elit.",
                "content": "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quasi labore ea exercitationem assumenda, minus ad.",
                "price": 32,
                "colors": ["red", "black", "crimson", "blue"],
                "count": 1
            },
        ],
        cart: [],
        total: []
    };


    addCart = (id) => {
        const { products, cart } = this.state;
        const check = cart.every(item => {
            return item._id !== id
        })
        if (check) {
            const data = products.filter(product => {
                return product._id === id
            })
            this.setState({ cart: [...cart, ...data] })
        } else {
            alert("The product has been added to cart!!!")
        }
    }

    reduction = id => {
        const { cart } = this.state;
        cart.forEach(item => {
            if (item._id === id) {
                item.count === 1 ? item.count = 1 : item.count -= 1;
            }
        })
        this.setState({ cart: cart });
        this.getTotal();
    }

    increase = id => {
        const { cart } = this.state;
        cart.forEach(item => {
            if (item._id === id) {
                item.count += 1;
            }
        })
        this.setState({ cart: cart });
        this.getTotal();
    }

    removeProduct = id => {
        if (window.confirm("Do you want to delete this product?")) {
            const { cart } = this.state;
            cart.forEach((item, index) => {
                if (item._id === id) {
                    cart.splice(index, 1)
                }
            })
            this.setState({ cart: cart });
            this.getTotal();
        }
    };

    getTotal = () => {
        const { cart } = this.state;
        const res = cart.reduce((prev, item) => {
            return prev + (item.price * item.count);
        }, 0);
        this.setState({ total: res });
        // this.getTotal();
    }

    componentDidUpdate() {
        localStorage.setItem('dataCart', JSON.stringify(this.state.cart))
        localStorage.setItem('dataTotal', JSON.stringify(this.state.total))
    }
    componentDidMount() {
        const dataCart = JSON.parse(localStorage.getItem('dataCart'));
        if (dataCart !== null) {
            this.setState({cart: dataCart})
        }
        const dataTotal = JSON.parse(localStorage.getItem('dataTotal'));
        if (dataTotal !== null) {
            this.setState({total: dataTotal})
        }
    }


    render() {

        const { products, cart, total } = this.state;
        const { addCart, reduction, increase, removeProduct, getTotal } = this;

        return (
            <DataContext.Provider value={{ products, addCart, cart, reduction, increase, removeProduct, total, getTotal }}>
                {this.props.children}
            </DataContext.Provider>
        )
    }
}
